import React, { useState, useEffect } from 'react';
import errorMessages from './error-messages';
import spinner from './spinner.svg';
import { fetchLists } from './services';

const Messages = (messagesList) => {
    // const [messagesList, setMessagesList] = useState({});
    // const [error, setError] = useState('');

    // useEffect( () => {
    // fetchLists()
    // .then( (usersMessagesList) => {
    //     setMessagesList(usersMessagesList.messagesList) ; // inform parent
    // })
    // .catch( (err) => {
    //   setError(errorMessages[err.code || 'DEFAULT']);
    // });
    // }, []);

    return (
        <div>
           <ol className="messages"> 
                    {messagesList && messagesList.length> 0 ? messagesList.map( message => (
                        <li key={message.username}>
                        <div className="message">
                            <div className="meta-info">
                            <div className="sender-info">
                                <span className="username">{message.username}</span>
                            </div>
                            <div className="message-info">
                                <span className="time-stamp">{message.timestamp}</span>
                            </div>
                            </div>
                            <p className="message-text">{message.text}</p>
                        </div>
                        </li>
                    )):null}
                </ol>
        </div>
    )
}
export default Messages;
