import React, { useState, useEffect } from 'react';

import { fetchLoginStatus } from './services';
  
// import {
//   fetchLogIn,
//   fetchLoginStatus,
//   fetchLists,
//   fetchSendMessage,
//   fetchLogout
// } from './services';

import Nav from './Nav';
import MessagesUsers from './MessagesUsers';
import Login from './Login';
import './app.css';

const App = () => {
  const [userState, setUserState] = useState({ isLoggedIn: false });

  // Runs when first rendered
  // AND whenever any vars in the passed array change
  // (None in this case)
  useEffect( () => {
    fetchLoginStatus()
    .then( userInfo => {
      fetchLists()
      .then( (usersMessagesList) => {
        setUserState({
          isLoggedIn: true,
          username: userInfo.username,
          messages: usersMessagesList.messagesList,
          users: usersMessagesList.usersList,
        });
        onLogin(userInfo.username, usersMessagesList.messagesList, usersMessagesList.usersList);
        // setMessagesList(usersMessagesList.messagesList) ; 
        // setUsersList(usersMessagesList.usersList);
      })
      .catch( (err) => {
        setError(errorMessages[err.code || 'DEFAULT']);
      });
    });
  }, []);

  const login = (username, messages, users) => {
    setUserState({
      isLoggedIn: true,
      username,
      messages,
      users
    });
  };

  const logout = () => {
    setUserState({
      isLoggedIn: false
    });
  };

  let content;

  if(userState.isLoggedIn) {
    content = <MessagesUsers user={userState}/>;
  } else {
    content = <Login onLogin={ login }/>;
  }

  return (
    <div className="app">
      <p className="heading"> Chat App </p>
      <Nav user={userState} onLogout={logout}/>
      {content}
    </div>
  );
};

export default App;
