    body: JSON.stringify({ messageText }),
  })
  .catch( () => {
    return Promise.reject({code: 'NETWORK_ERROR'});
  })
  .then( (response) => {
    if(!response.ok) {
        return response.json().then( convertServiceError );
    }
    return response.json();
  });
};

export const fetchLogout = () => {
  return fetch('/session', {
    method: 'DELETE',
  })
  .catch( () => {
    return Promise.reject({code: 'NETWORK_ERROR'});
  })
  .then( (response) => {
    if(!response.ok) {
      return response.json().then( convertServiceError );
    }
    return response.ok;
  });
};
