import React, { useState, useEffect } from 'react';
import errorMessages from './error-messages';
import spinner from './spinner.svg';
import { fetchLists } from './services';
import SendMessage from './SendMessage';
import Messages from './GetMessagesList';
import Users from './GetUsersList';

const MessagesUsersList = ({ user }) => {
  const [usersList, setUsersList] = useState({});
  const [messagesList, setMessagesList] = useState({});
  const [error, setError] = useState('');

    // useEffect( () => {
    fetchLists()
    .then( (usersMessagesList) => {
        console.log(usersMessagesList.messagesList);
        console.log(usersMessagesList.usersList);
        setMessagesList(usersMessagesList.messagesList) ; 
        setUsersList(usersMessagesList.usersList);
    })
    .catch( (err) => {
      setError(errorMessages[err.code || 'DEFAULT']);
    });
    // }, []);

console.log(usersList);
console.log(messagesList);

  return (
    <ul className='nav'>
        <SendMessage/>
        <Users userList={usersList}/>
        <Messages messages={messagesList}/>
    </ul>
  );
};

export default MessagesUsersList;
