import React, { useState } from 'react';
import { fetchLogin, fetchLists } from './services';
import errorMessages from './error-messages';
import spinner from './spinner.svg';

const Login = ({ onLogin }) => {

  const [username, setUsername] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const performLogin = () => {
    if(!username) {
      setError(errorMessages.USERNAME_REQUIRED);
      return;
    }
    setError('');
    setIsLoading(true);
    fetchLogin(username)
    .then( (userInfo) => {
      setUsername('');
      fetchLists()
      .then( (usersMessagesList) => {
        onLogin(userInfo.username, usersMessagesList.messagesList, usersMessagesList.usersList);
      })
      .catch( (err) => {
        setError(errorMessages[err.code || 'DEFAULT']);
      });
    })
    .catch( (err) => {
      setError(errorMessages[err.code || 'DEFAULT']);
      setIsLoading(false);
    });
  };

  return (
    <div className="login">
      <p className="error">{error}</p>
      <input value={username} className="username" onChange={ (e) => setUsername(e.target.value) }/>
      { isLoading ?
          <img alt="spinner" src={spinner}/> :
          <button className="login-button" onClick={ performLogin }>Login</button>
      }
    </div>
  );

};

export default Login;
