import React, { useState } from 'react';
import errorMessages from './error-messages';
import spinner from './spinner.svg';
import { fetchSendMessage } from './services';

const SendMessage = () => {

  const [message, setMessage] = useState('');
//   const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const performAction = () => {
    if(!message) {
      setError(errorMessages.USERNAME_REQUIRED);
      return;
    }
    // set output for while we wait
    setError('');
    // setIsLoading(true);
    // start service call
    fetchSendMessage(message)
    .then( (messageInfo) => {
      setMessage(messageInfo.message); // inform parent
    })
    .catch( (err) => {
      setError(errorMessages[err.code || 'DEFAULT']);
    //   setIsLoading(false);
    });
  };

  return (
    <div className="send-message">
      <p className="error">{error}</p>
      <input className="to-send-text" onChange={ (e) => setMessage(e.target.value) }/>
      {/* { isLoading ? */}
          {/* <img alt="spinner" src={spinner}/> : */}
          <button className="send-message-button" onClick={ performAction }>Send Message</button>
      {/* } */}
    </div>
  );

    // return (
    //     <div>
    //         <input className="to-send" placeholder="Enter message to send"/>
    //         <button className="send-button">Send</button>
    //     </div>
    // )
}
export default SendMessage
