import React, { useState, useEffect } from 'react';
import errorMessages from './error-messages';
import spinner from './spinner.svg';
import { fetchLists } from './services';

const Messages = () => {
    const [messagesList, setMessagesList] = useState({});
    const [error, setError] = useState('');

    useEffect( () => {
    fetchLists()
    .then( (usersMessagesList) => {
        setMessagesList(usersMessagesList.messagesList) ; // inform parent
    })
    .catch( (err) => {
      setError(errorMessages[err.code || 'DEFAULT']);
    });
    }, []);

    return (
        <div>
           <ol class="messages"> 
           {console.log(messagesList)}
                    {messagesList && messagesList.length> 0 ? messagesList.map( message => (
                        <li>
                        <div class="message">
                            <div class="meta-info">
                            <div class="sender-info">
                                <span class="username">{message.username}</span>
                            </div>
                            <div class="message-info">
                                <span class="time-stamp">{message.timestamp}</span>
                            </div>
                            </div>
                            <p class="message-text">{message.text}</p>
                        </div>
                        </li>
                    )):null}
                </ol>
        </div>
    )
}
export default Messages;
