const convertNetworkError = (err) => {
    return {
      code: 'NETWORK-ERROR',
      err
    };
  };

  const convertServiceError = (err) => Promise.reject(err);
  
  export const fetchLoginStatus = (username) => {
    return fetch('/session', {
      method: 'GET',
    })
    .catch( convertNetworkError )
    .then( response => {
      if(!response.ok) {
        return response.json().then(convertServiceError);
      }
      return response.json();
    });
  };
  
  export const fetchLogin = (username) => {
    return fetch('/session', {
      method: 'POST',
      headers: new Headers({
        'content-type': 'application/json',
      }),
      body: JSON.stringify({ username }),
    })
    .catch( convertNetworkError )
    .then( response => {
      if(!response.ok) {
        return response.json().then( convertServiceError );
      }
      return response.json();
    });
  };

  export const fetchLists = () => {
    return fetch('/lists', {
      method: 'GET',
    })
    .catch( convertNetworkError )
    .then( (response) => {
      if(!response.ok) {
        return response.json().then( convertServiceError );
      }
      return response.json();
    });
  };

  export const fetchSendMessage = (messageText) => {
    return fetch('/message', {
      method: 'POST',
      headers: new Headers({
        'content-type': 'application/json',
      }),
      body: JSON.stringify({ messageText }),
    })
    .catch( (response) => {
      console.log(response);
      return Promise.reject({code: 'network-error'});
    })
    .then( (response) => {
      console.log(response);
      if(!response.ok) {
        // if(response.status === 500){
        //   return Promise.reject({code: 'NETWORK_ERROR'});
        // }else{
          return response.json().then( convertServiceError );
        // }
      }
      return response.json();
    });
  };
  
  export const fetchLogout = () => {
    return fetch('/session', {
      method: 'DELETE',
    })
    .catch( convertNetworkError)
    .then( (response) => {
      if(!response.ok) {
        return response.json().then( convertServiceError );
      }
      return response.ok;
    });
  };
